#include <regex>

std::string markdown_to_html(const std::string& markdown) {
    std::stringstream ss;
    std::string html;
    std::regex regex("\\\\|[|](.*?)[\\|]\\|");
    std::smatch match;
    while(regex_search(markdown, match, regex)) {
        html += "<a href=\"#\" onclick=\"window.open('\\2');window.close()\">\\2</a>";
    }
    ss << html;
    return ss.str();
}
